mapper.select:
select count(*) from science


    sciencestarttime LIKE  CONCAT('${scienceTime}','%')


  AND  sciencetype = #{scienceType}


    AND  stunum = #{studentNumber}


    AND  sciencelevel = #{scienceLevel}

select * from science


    sciencestarttime LIKE  CONCAT('${scienceTime}','%')


    AND  sciencetype = #{scienceType}


    AND  stunum = #{studentNumber}


    AND  sciencelevel = #{scienceLevel}


limit #{pageBean.start},#{pageBean.pageSize}
